/////////////////////////////////////////////////////////////////////////////
// CDemoView drawing

void CDemoView::OnDraw(CDC* pDC)
{
	//初始坐标
	CPoint point(10, 10);

	for (int n = 0; n < 10; n++)
	{
		int nHeight = n * 5 + 10;

		//创建字体
		CFont font;
		font.CreateFont(
			nHeight,
			0,
			0,
			0,
			FW_NORMAL,
			FALSE,
			FALSE,
			0,
			ANSI_CHARSET,
			OUT_DEFAULT_PRECIS,
			CLIP_DEFAULT_PRECIS,
			DEFAULT_QUALITY,
			DEFAULT_PITCH | FF_SWISS,
			_T("Arial"));

		//将字体选入设备环境
		CFont* pOldFont = pDC->SelectObject(&font);

		pDC->TextOut(point.x, point.y, _T("Hello World!"));

		pDC->SelectObject(pOldFont);

		point.y += nHeight;
	}
}

